  describe "GET new" do
    it "assigns a new other_liability as @other_liability" do
      get :new
      assigns(:other_liability).should be_a_new(OtherLiability)
    end
  end

  describe "GET edit" do
    it "assigns the requested other_liability as @other_liability" do
      get :edit, :id => other_liability.id.to_s
      assigns(:other_liability).should eq(other_liability)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new OtherLiability" do
        expect {
          post :create, :other_liability => valid_attributes
        }.to change(OtherLiability, :count).by(1)
      end

      it "assigns a newly created other_liability as @other_liability" do
        post :create, :other_liability => valid_attributes
        assigns(:other_liability).should be_a(OtherLiability)
        assigns(:other_liability).should be_persisted
      end

      it "redirects to the created other_liability" do
        post :create, :other_liability => valid_attributes
        response.should redirect_to(OtherLiability.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved other_liability as @other_liability" do
        # Trigger the behavior that occurs when invalid params are submitted
        OtherLiability.any_instance.stub(:save).and_return(false)
        post :create, :other_liability => {}
        assigns(:other_liability).should be_a_new(OtherLiability)
      end

      pending "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        OtherLiability.any_instance.stub(:save).and_return(false)
        post :create, :other_liability => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested other_liability" do
