        OtherLiability.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => other_liability.id, :other_liability => {'these' => 'params'}
      end

      it "assigns the requested other_liability as @other_liability" do
        put :update, :id => other_liability.id, :other_liability => valid_attributes
